import "forge-std/console2.sol";

contract MockFallback is IFallback {
    function delegateCallTarget(
        uint256 param
    ) public view returns (uint256 _param, address sender, address _this) {
        console2.log(
            "delegateCallTarget called with param: %s msg.sender: %s this: %s",
            param,
            msg.sender,
            address(this)
        );
        return (param, msg.sender, address(this));
    }

    function callTarget(
        uint256 param
    )
        public
        view
        returns (
            uint256 _param,
            address sender,
            address er2771Sender,
            address _this
        )
    {
        console2.log(
            "callTarget called with param: %s msg.sender: %s this: %s",
            param,
            msg.sender,
            address(this)
        );
        return (param, msg.sender, _msgSender(), address(this));
    }

    function staticCallTarget(
        uint256 param
    )
        public
        view
        returns (
            uint256 _param,
            address sender,
            address er2771Sender,
            address _this
        )
    {
        console2.log(
            "staticCall called with param: %s msg.sender: %s this: %s",
            param,
            msg.sender,
            address(this)
        );
        return (param, msg.sender, _msgSender(), address(this));
    }

    function _msgSender() internal pure returns (address sender) {
